INSERT INTO `weibo_comments`(`commentId`, `userName`, `userFans`, `userComments`, `comments_1`, `Link`, `keyWord`) VALUES (2038, '香港文匯報', '161751', '【二次确诊人数呈上升趋势】昨日新增8,488宗确诊个案。传染力极强的新变异病毒BA.4或BA.5已成为新冠病毒主流。卫生防护中心昨日表示，近日二次确诊人数有上升趋势。特区政府专家顾问刘宇隆相信，香港的BA.5个案占比会持续增加至90%，每日新增确诊个案或上升至两万宗。<a  href=\"https://weibo.cn/sinaurl?u=https%3A%2F%2Fwww.wenweipo.com%2Fa%2F202208%2F30%2FAP630d264de4b033218a5fd11d.html\" data-hide=\"\"><span class=\'url-icon\'><img style=\'width: 1rem;height: 1rem\' src=\'https://h5.sinaimg.cn/upload/2015/09/25/3/timeline_card_small_web_default.png\'></span><span class=\"surl-text\">【抗擊新冠肺炎】二次確診人數呈上升趨勢 BA.5個案料持續增至90% 專家倡推第四階段「疫苗通」</span></a> ', '【二次确诊人数呈上升趋势】昨日新增8,488宗确诊个案。传染力极强的新变异病毒BA.4或BA.5已成为新冠病毒主流。卫生防护中心昨日表示，近日二次确诊人数有上升趋势。特区政府专家顾问刘宇隆相信，香港的BA.5个案占比会持续增加至90%，每日新增确诊个案或上升至两万宗。【抗擊新冠肺炎】二次確診人數呈上升趨勢 BA.5個案料持續增至90% 專家倡推第四階段「疫苗通」', 'https://weibo.cn/sinaurl?u=https%3A%2F%2Fwww.wenweipo.com%2Fa%2F202208%2F30%2FAP630d264de4b033218a5fd11d.html', '新冠');
